import{_ as t,c as n,a2 as a,o as r}from"./chunks/framework.BQmytedh.js";const s="/public_daz/pictures/Modalverben_A1.jpg",g=JSON.parse('{"title":"Modalverben im Deutschen","description":"","frontmatter":{},"headers":[],"relativePath":"students/daz/grammar/daz_modals.md","filePath":"students/daz/grammar/daz_modals.md","lastUpdated":1734455789000}'),i={name:"students/daz/grammar/daz_modals.md"};function o(d,e,u,h,l,c){return r(),n("div",null,e[0]||(e[0]=[a('<h1 id="modalverben-im-deutschen" tabindex="-1">Modalverben im Deutschen <a class="header-anchor" href="#modalverben-im-deutschen" aria-label="Permalink to &quot;Modalverben im Deutschen&quot;">‚Äã</a></h1><p><strong>Modalverben</strong> beschreiben die Art und Weise, <strong>wie</strong> etwas getan wird, n√§her. So kann man Aktionen oder Dinge die man tut, genauer beschreiben. Anstatt zu sagen: &quot;Ich gehe nach Hause&quot; kann man erkl√§ren &quot;Ich <strong>muss</strong> nach Hause <strong>gehen</strong>.&quot; Schon wei√ü die andere Person genauer, was du denkst. Den Name <strong>Modalverben</strong> kann man sich gut merken, wenn man an das Wort &quot;Modus&quot; denkt. Wenn etwas in einem bestimmten Modus ist (z.B. bei einem Computerspiel) dann wird es auf eine bestimmte Art gemacht (Zweispielermodus -&gt; Spielen zu zweit).</p><h2 id="einsteiger" tabindex="-1">Einsteiger <a class="header-anchor" href="#einsteiger" aria-label="Permalink to &quot;Einsteiger&quot;">‚Äã</a></h2><p>Viele Modalverben kennst du bereits. &quot;Ich <strong>muss</strong> mich beeilen&quot; oder &quot;Du <strong>darfst</strong> da nicht nicht schwimmen.&quot; Eigentlich bist du schon echte Expert*in! Wir wollen dein Vorwissen nun Schritt f√ºr Schritt systematisieren. <img src="'+s+'" alt="Quelle: https://deutschlernerblog.de/modalverben-uebungen-a1-bis-c2/"></p><div class="info custom-block"><p class="custom-block-title">Aufgabe 1</p><p>Schau dir die bilden genau an und versuch zu erkennen, <strong>wie</strong> die Menschen auf den Bildern etwas tun. Denke daran, dass Modalverben die Art und Weise wie etwas passiert, n√§her beschreiben.</p></div><h3 id="etwas-durfen" tabindex="-1">etwas d√ºrfen <a class="header-anchor" href="#etwas-durfen" aria-label="Permalink to &quot;etwas d√ºrfen&quot;">‚Äã</a></h3><p>Das kennen die meisten Menschen, jung und alt:</p><ul><li>&quot;Du <strong>darfst</strong> das nicht!&quot;</li><li>&quot;Papa hat gesagt, dass ich das nicht <strong>darf</strong>.&quot;</li><li>&quot;Ich <strong>durfte</strong> fr√ºher nicht viel Cola trinken.&quot;&quot; Etwas zu <strong>d√ºrfen</strong> bedeutet, dass eine andere Person <em>nicht</em> verboten hat, dass etwas getan werden darf. Oder andersherum: wenn man etwas <em>nicht</em> darf, dann hat eine andere Person die verboten, etwas zu tun. | ‚û°Ô∏èkein Verbot | <em>du darfst</em> | |--------------------------|-------------------| | üö´Verbot | <em>du darfst nicht</em> |</li></ul><h2 id="fortgeschrittene" tabindex="-1">Fortgeschrittene <a class="header-anchor" href="#fortgeschrittene" aria-label="Permalink to &quot;Fortgeschrittene&quot;">‚Äã</a></h2><h2 id="profis" tabindex="-1">Profis <a class="header-anchor" href="#profis" aria-label="Permalink to &quot;Profis&quot;">‚Äã</a></h2>',10)]))}const b=t(i,[["render",o]]);export{g as __pageData,b as default};
